import axios from "axios";
import React from "react";
import { useState, useEffect } from "react";
import "../App.css";
import { useGetUserID } from "../hooks/getUserId";
const Home = () => {
  const userID = use
  const [recipes, setRecipes] = useState([]);

  useEffect(() => {
    const fetchRecipe = async () => {
      try {
        const response = await axios.get("http://localhost:3003/recipes");
        setRecipes(response.data);
        console.log((await response).data);
      } catch (error) {
        console.error("error is", error);
      }
    };
    fetchRecipe();
  }, []);

  const handleSaveRecipe = async (recipeId) => {
      try {
        const response = await axios.put("http://localhost:3003/recipes", {
          recipeId,userId
        });
        console.log(response);
      } catch (error) {
        console.error(error);
      }
  }

  return (
    <div>
      <h1>home</h1>
      <div >
      {recipes.map((recipe) => (
        <div key={recipe._id} className="recipe-card">
          <img
            src={recipe.imageUrl}
            alt=''
          />
          <br />
          <button onClick={() => handleSaveRecipe(recipe._id)} >Save</button>

          <p>{recipe.name}</p>
          <p>
            {recipe.ingredients.map((ingredient) => (
              <span key={ingredient.id}>{ingredient},</span>
            ))}
          </p>
          <p>{recipe.instructions}</p>
          <p>{recipe.cookingTime}</p>
        </div>
      ))}
      </div>
    </div>
  );
};

export default Home;
