import React, { useState } from "react";

const CreateRecipe = () => {
  const [recipe, setRecipe] = useState({
    name: "",
    ingredients: [],
    instructions: "",
    imageUrl: "",
    cookingTime: 0,
    userOwner: 0,
  });
  const handleChange = () => {

  }
  return (
    <div className='create-recipe'>
      <h2>Create Recipe</h2>
      <form action=''>
        <label htmlFor='name'>Name</label>
        <input
          type='text'
          id='name'
          onChange={handleChange}
        />

        <label htmlFor='instructions'>Instructions</label>
        <textarea
          name='instructions'
          id='instructions'
          cols='30'
          rows='10'
        ></textarea>
        <label htmlFor='imageUrl'>ImageUrl</label>
        <input
          type='text'
          id='imageUrl'
          name='imageUrl'
        />
        <label htmlFor='cookingTime'>Cooking Time (mints)</label>
        <input
          type='number'
          id='cookingTime'
          name='cookingTime'
        />
      </form>
    </div>
  );
};

export default CreateRecipe;
