import React, { useState } from "react";

const Auth = () => {
  return (
    <div className='auth'>
      Aut
      <Login />
      <Registration />
    </div>
  );
};

const Login = () => {
    return <>
     const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  return (
    <div className='auth-container'>
      <form>
        <h2>Register</h2>
        <div className='form-group'>
          <label htmlFor=''> username :</label>
          <input
            value={username}
            type='text'
            id='username'
            onChange={(event) => setUsername(event.target.value)}
          />
        </div>
        <div className='form-group'>
          <label htmlFor=''> PassWord :</label>
          <input
            value={password}
            type='text'
            id='PassWord'
            onChange={(event) => setPassword(event.target.value)}
          />
              </div>
              <button type="submit">Register</button>
      </form>
    </div>
  );</>;
};

const Registration = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  return (
    <div className='auth-container'>
      <form>
        <h2>Register</h2>
        <div className='form-group'>
          <label htmlFor=''> username :</label>
          <input
            value={username}
            type='text'
            id='username'
            onChange={(event) => setUsername(event.target.value)}
          />
        </div>
        <div className='form-group'>
          <label htmlFor=''> PassWord :</label>
          <input
            value={password}
            type='text'
            id='PassWord'
            onChange={(event) => setPassword(event.target.value)}
          />
              </div>
              <button type="submit">Register</button>
      </form>
    </div>
  );
};

export default Auth;
